   /** 
   * 将指定byte数组以16进制的形式打印到控制台 
   * @param hint String 
   * @param b byte[] 
   * @return void 
   */  
   public static void printHexString(String hint, byte[] b) {  
    System.out.print(hint);  
    for (int i = 0; i < b.length; i++) {  
     String hex = Integer.toHexString(b[i] & 0xFF);  
     if (hex.length() == 1) {  
      hex = '0' + hex;  
     }  
     System.out.print(hex.toUpperCase() + " ");  
    }  
    System.out.println("");  
   }  
   /** 
   * @param b byte[] 
   * @return String 
   */  
   public static String Bytes2HexString(byte[] b) {  
    String ret = "";  
    for (int i = 0; i < b.length; i++) {  
     String hex = Integer.toHexString(b[i] & 0xFF);  
     if (hex.length() == 1) {  
      hex = '0' + hex;  
     }  
     ret += hex.toUpperCase();  
    }  
    return ret;  
   }  
   /** 
   * 将两个ASCII字符合成一个字节； 如："EF"--> 0xEF 
   * @param src0 byte 
   * @param src1 byte 
   * @return byte 
   */  
   public static byte uniteBytes(byte src0, byte src1) {  
    byte _b0 = Byte.decode("0x" + new String(new byte[] { src0 }))  
      .byteValue();  
    _b0 = (byte) (_b0 << 4);  
    byte _b1 = Byte.decode("0x" + new String(new byte[] { src1 }))  
      .byteValue();  
    byte ret = (byte) (_b0 ^ _b1);  
    return ret;  
   }  
   /** 
   * 将指定字符串src，以每两个字符分割转换为16进制形式 如："2B44EFD9" --> byte[]{0x2B, 0x44, 0xEF, 0xD9} 
   * @param src String 
   * @return byte[] 
   */  
   public static byte[] HexString2Bytes(String src) {  
    byte[] ret = new byte[8];  
    byte[] tmp = src.getBytes();  
    for (int i = 0; i < 8; i++) {  
     ret[i] = uniteBytes(tmp[i * 2], tmp[i * 2 + 1]);  
    }  
    return ret;  
   }  
   
   /** 
     * byte数组转换成16进制字符数组
        * @param src 
     * @return 
       */  
     public static String[] bytesToHexStrings(byte[] src){       
             if (src == null || src.length <= 0) {       
                  return null;       
             }
            String[] str = new String[src.length];
             
              for (int i = 0; i < src.length; i++) {       
                   int v = src[i] & 0xFF;       
                  String hv = Integer.toHexString(v);       
                 if (hv.length() < 2) {       
                       str[i] = "0";       
                 }       
               str[i] = hv;        
              }       
               return str;       
          }
     
     
     public static byte[] hexStringToBytes(String hexString) {
 		if ((hexString == null) || (hexString.equals(""))) {
 			return null;
 		}
 		hexString = hexString.toUpperCase();
 		int length = hexString.length() / 2;
 		char[] hexChars = hexString.toCharArray();
 		byte[] d = new byte[length];
 		for (int i = 0; i < length; ++i) {
 			int pos = i * 2;
 			d[i] = (byte) (charToByte(hexChars[pos]) << 4 | charToByte(hexChars[(pos + 1)]));
 		}
 		return d;
 	}

 	private static byte charToByte(char c) {
 		return (byte) "0123456789ABCDEF".indexOf(c);
 	}
     //字节数组转换成16进制串
     public static String byte2HexStr(byte[] b, int length)
     {
     String hs = "";
     String stmp = "";
     for (int n = 0; n < length; ++n) {
     stmp = Integer.toHexString(b[n] & 0xFF);
     if (stmp.length() == 1)
     hs = hs + "0" + stmp;
     else {
     hs = hs + stmp;
     }
     }
     return hs.toUpperCase();
     } 
     
     
     public static final String encodeHex(String msg) {
    	 byte[] bytes = null;
    	 try {
    	 bytes = msg.getBytes("GBK");
    	 } catch (java.io.UnsupportedEncodingException e) {
    	 e.printStackTrace();
    	 }
    	 StringBuffer buff = new StringBuffer(bytes.length * 4);
    	 String b;
    	 char a;
    	 int n = 0;
    	 int m = 0;
    	 for (int i = 0; i < bytes.length; i++) {
    	 b = Integer.toHexString(bytes[i]);
    	 if (bytes[i] > 0) {
    	 buff.append("00");
    	 buff.append(b);
    	 n = n + 1;
    	 } else {
    	 a = msg.charAt((i - n) / 2 + n);
    	 m = a;
    	 b = Integer.toHexString(m);
    	 buff.append(b.substring(0, 4));

    	 i = i + 1;
    	 }
    	 }
    	 return buff.toString();
    	 }
